import { Component, ViewChild } from '@angular/core';
import { YonlendirmeButonuComponent } from 'src/app/shared/yonlendirme-butonu/yonlendirme-butonu.component';
@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  @ViewChild('first') YonlendirmeButonuComponent!: YonlendirmeButonuComponent;
}
<style>
  @import url('https://fonts.googleapis.com/css2?family=Comme:wght@500&display=swap');
  @import url('https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@200&display=swap');
  @import url('https://fonts.googleapis.com/css2?family=Yaldevi:wght@500&display=swap');
</style>
<div>
  <header class="websiteHeader">
    <h1 class="headerTitle" style=" font-family:'Yaldevi', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;">.:: Gazi Havacılık ::.</h1>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Anasayfa'"></app-yonlendirme-butonu>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Hakkımızda'"></app-yonlendirme-butonu>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Eğitim'"></app-yonlendirme-butonu>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Tüzük'"></app-yonlendirme-butonu>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Bilgi Sistemi'"></app-yonlendirme-butonu>
    <app-yonlendirme-butonu class="butonlar"[buttonText]="'Blog'"></app-yonlendirme-butonu>
  </header>
</div>
